$laugh_ico_base64=''


function AddPortMapping {
    $wslIP = ((wsl.exe -- bash -c "ifconfig eth0 | grep 'inet ' | awk '{print $2}'").Trim() -split " ")[1]
    $localPort = Read-Host "Please input local listening port"
    $localIP = Read-Host "Please input local listening IP Address (default 0.0.0.0)"

    $remotePort = Read-Host "Please input remote forward port (default $localPort)"
    if ($remotePort -eq ""){
        $remotePort = $localPort
    }

    $remoteIP = Read-Host "Please input remote forward ip (default WSL2 IP $wslIP)"
    if ($remoteIP -eq "") {
        $remoteIP = $wslIP
    }
    if ($localIP -eq ""){
        $localIP = "0.0.0.0"
    }

    $result = "Mapping local port $localPort($localIP) to $remotePort($RemoteIP)"
    Write-Host $result
    netsh interface portproxy add v4tov4 listenport=$localPort listenaddress=$localIP connectport=$remotePort connectaddress=$remoteIP tcp
    $rule = New-Object -TypeName PSObject
    $rule | Add-Member -MemberType NoteProperty -Name "LocalIP" -Value $localIP
    $rule | Add-Member -MemberType NoteProperty -Name "LocalPort" -Value $localPort
    $rule | Add-Member -MemberType NoteProperty -Name "RemotePort" -Value $remotePort
    $rule | Add-Member -MemberType NoteProperty -Name "RemoteIP" -Value $remoteIP
    $portMappings.Add($rule)

    $title = "Port forward create success"
    Write-Host $title
    ShowPortMappings
    Show-Notification -ToastTitle "$title" -ToastText "$result"
}

function RemovePortMapping {
    ShowPortMappings

    $index = Read-Host "Input sequence number to delete"
    $rule = $portMappings[$index]
    $result = "Deleting map local port $($rule.LocalPort)($($rule.LocalIP)) to $($rule.RemotePort)($($rule.RemoteIP))"
    Write-Host $result
    netsh interface portproxy delete v4tov4 listenaddress=$($rule.LocalIP) listenport=$($rule.LocalPort)

    $portMappings.Remove($rule)

    $title = "Delete success"
    Write-Host $title
    ShowPortMappings
    Show-Notification -ToastTitle "$title" -ToastText "$result"
}

function RetryPortMapping {
    ShowPortMappings

    $index = Read-Host "Input sequence number to remapping"
    $rule = $portMappings[$index]
    Write-Host $portMappings.Count

    $result = "Remapping map local port $($rule.LocalPort)($($rule.LocalIP)) to $($rule.RemotePort)($($rule.RemoteIP))"
    Write-Host $result
    netsh interface portproxy delete v4tov4 listenaddress=$($rule.LocalIP) listenport=$($rule.LocalPort)

    netsh interface portproxy add v4tov4 listenport=$($rule.LocalPort) listenaddress=$($rule.LocalIP) connectport=$($rule.RemotePort) connectaddress=$($rule.RemoteIP) tcp

    $title = "Remapping success"
    Write-Host $title
    ShowPortMappings
    Show-Notification -ToastTitle "$title" -ToastText "$result"
}

function ShowPortMappings1 {
    if ($portMappings) {
        for ($i = 0; $i -lt $portMappings.Count; $i++) {
            $rule = $portMappings[$i]
            Write-Host "$i. $($rule.LocalPort) ($($rule.LocalIP)) -> $($rule.RemotePort) ($($rule.RemoteIP))"
        }
    }
    else {
        $result = "Empty port forward list"
        Write-Host $result
        Show-Notification -ToastTitle "" -ToastText "$result"
    }
}

function ShowPortMappings {
    Clear-Host
    Write-Host "Current port mappings:"
    $output = Invoke-Expression "netsh interface portproxy show v4tov4"
    $lines = $output -split "`r`n" | Select-Object -Skip 5 | Select-Object -SkipLast 1
    $portMappings.Clear()
    foreach ($line in $lines) {
        $columns = $line -split "\s+"
        $localIp = $columns[0]
        $localPort = $columns[1]
        $remoteIp = $columns[2]
        $remotePort = $columns[3]

        $rule = New-Object -TypeName PSObject
        $rule | Add-Member -MemberType NoteProperty -Name "LocalIP" -Value $localIp
        $rule | Add-Member -MemberType NoteProperty -Name "LocalPort" -Value $localPort
        $rule | Add-Member -MemberType NoteProperty -Name "RemotePort" -Value $remotePort
        $rule | Add-Member -MemberType NoteProperty -Name "RemoteIP" -Value $remoteIP
        $portMappings.Add($rule) | Out-Null
    }
    ShowPortMappings1
}

function ShowMainMenu {
    Write-Host "Select operation"
    Write-Host "1. Create new port forward"
    Write-Host "2. Delete port forward"
    Write-Host "3. List port forward"
    Write-Host "4. Remap port forward"
    Write-Host "e. Exit"

    $choice = Read-Host

    switch ($choice) {
        "1" { AddPortMapping }
        "2" { RemovePortMapping }
        "3" { ShowPortMappings }
        "4" { RetryPortMapping }
        "e" { exit }
        default { Write-Host "Invalid option" }
    }

    ShowMainMenu
}
ShowAsciiArt
ShowMainMenu


### toast ###

function Show-Notification {
    [cmdletbinding()]
    Param (
        [string]
        $ToastTitle,
        [string]
        [parameter(ValueFromPipeline)]
        $ToastText,
        [int]
        $ToastExpireSecond = 60,
        [string]
        $image = 'G:\pf\laugh.ico'
    )

    if (!(Test-Path $image)) {
        #  Base64 
        $imageData = [System.Convert]::FromBase64String($laugh_ico_base64)
        [System.IO.File]::WriteAllBytes($image, $imageData)
        Write-Host "Icon file created successfully: $image"
    }else{
        Write-Host "Icon file exists: $image"
    }

    $ToastImageAndText02 = [Windows.UI.Notifications.ToastTemplateType, Windows.UI.Notifications, ContentType = WindowsRuntime]::ToastImageAndText02
    $TemplateContent = [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]::GetTemplateContent($ToastImageAndText02)
    $TemplateContent.SelectSingleNode('//image[@id="1"]').SetAttribute('src', $image)
    $TemplateContent.SelectSingleNode('//text[@id="1"]').InnerText = $ToastTitle
    $TemplateContent.SelectSingleNode('//text[@id="2"]').InnerText = $ToastText

    $RawXml = [xml] $TemplateContent.GetXml()
    $SerializedXml = New-Object Windows.Data.Xml.Dom.XmlDocument
    $SerializedXml.LoadXml($RawXml.OuterXml)

    $Toast = [Windows.UI.Notifications.ToastNotification]::new($SerializedXml)
    $Toast.ExpirationTime = [DateTimeOffset]::Now.AddSeconds($ToastExpireSecond)

    $AppId = '{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\WindowsPowerShell\v1.0\powershell.exe'
    [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($AppId).Show($Toast)
}

$portMappings = New-Object System.Collections.ArrayList

